package fs

import (
	"bufio"
	"os"
	"path/filepath"
	"testing"

	"github.com/klauspost/pgzip"
	. "github.com/smartystreets/goconvey/convey"
	"github.com/wtsi-ssg/wrstat/v3/combine"
)

const longFileContent = "!" //nolint

func TestFS(t *testing.T) {
	expectedOutput := longFileContent + longFileContent + "\n"

	Convey("Given two files each containing a line over 65536 characters long, they can be concatenated and compressed.", t, func() {
		inputs, output, outputPath := buildTestFiles(t)

		err := combine.ConcatenateAndCompress(inputs, output)
		So(err, ShouldBeNil)

		_, err = os.Stat(outputPath)
		So(err, ShouldBeNil)

		Convey("With a default buffer length, the content cannot be read.", func() {
			actualContent, err := readCompressedFileWithArbitraryBuffer(outputPath, bufio.MaxScanTokenSize)
			So(err, ShouldBeNil)

			So(actualContent, ShouldNotEqual, expectedOutput)
		})

		Convey("But with a large buffer length, the content can be read.", func() {
			actualContent, err := readCompressedFileWithArbitraryBuffer(outputPath, 1000000)
			So(err, ShouldBeNil)

			So(actualContent, ShouldEqual, expectedOutput)
		})
	})
}

// readCompressedFileWithArbitraryBuffer is a copy of fs.ReadCompressedFile, but
// has the option to change the buffer size, so as to demonstrate the difference
// in content that can be read depending upon the buff size.
func readCompressedFileWithArbitraryBuffer(filePath string, bufferSize int) (string, error) {
	actualFile, err := os.Open(filePath)
	if err != nil {
		return "", err
	}

	fileReader, err := pgzip.NewReader(actualFile)
	if err != nil {
		return "", err
	}

	defer fileReader.Close()

	fileScanner := bufio.NewScanner(fileReader)
	fileScanner.Buffer([]byte{}, bufferSize)

	var fileContents string
	for fileScanner.Scan() {
		fileContents += fileScanner.Text() + "\n"
	}

	return fileContents, nil
}

// buildTestFiles builds two files, each with a line over 65536 chars long.
func buildTestFiles(t *testing.T) ([]*os.File, *os.File, string) {
	t.Helper()
	dir := t.TempDir()

	longfile1, err := os.Create(filepath.Join(dir, "longfile1"))
	So(err, ShouldBeNil)
	longfile2, err := os.Create(filepath.Join(dir, "longfile2"))
	So(err, ShouldBeNil)

	_, err = longfile1.WriteString(longFileContent)
	So(err, ShouldBeNil)
	_, err = longfile2.WriteString(longFileContent)
	So(err, ShouldBeNil)

	filenames, err := FindFilePathsInDir(dir, "")
	So(err, ShouldBeNil)

	inputs, err := OpenFiles(filenames)
	So(err, ShouldBeNil)

	outputPath := filepath.Join(dir, "output")

	fo, err := os.Create(outputPath)
	if err != nil {
		t.Fatalf("create error: %s", err)
	}

	return inputs, fo, outputPath
}
